bool left, right;

void Bongo_Disp(void){
    ST7735_DrawBitmapBongo(0, 82, bongocat1, BONGOCAT1_WIDTH, BONGOCAT1_HEIGHT);
	
    if (!left){
        ST7735_DrawBitmapBongo(BONGOCAT2_LEFT_X, BONGOCAT2_LEFT_HEIGHT+BONGOCAT2_LEFT_Y, bongocat2_left, BONGOCAT2_LEFT_WIDTH, BONGOCAT2_LEFT_HEIGHT);
    } 
    if (!right){
        ST7735_DrawBitmapBongo(BONGOCAT2_RIGHT_X, BONGOCAT2_RIGHT_HEIGHT+BONGOCAT2_RIGHT_Y, bongocat2_right, BONGOCAT2_RIGHT_WIDTH, BONGOCAT2_RIGHT_HEIGHT);
    }

}

void App_BongoCat_Load(void){
	ST7735_InitR(INITR_REDTAB);
    ST7735_FillScreen(ST7735_BLACK);
    ST7735_SetRotation(2);
    left = false;
    right = false;
    Bongo_Disp();
}
	
void App_BongoCat_KeyPress(uint8_t usageCode){
    left ^= 1;
    right ^= 1;
    Bongo_Disp();
}

void App_BongoCat_KeyRelease(uint8_t usageCode){
    return;
}

